def count(text):
    freq = {}
    for str in text.split():
        if str in freq:
            freq[str] += 1
        else:
            freq[str] = 0
    return freq


def main():
    ttr = count(lorem_ipsum)
    wordCount = len(lorem_ipsum.split())
    for key in ttr.keys():
        ttr[key] /= wordCount

    # TTR is the ratio obtained by dividing the types (the total number of different words)
    # occurring in a text or utterance by its tokens (the total number of words).
    result = len(ttr) / wordCount
    print(result)  # 0.1295

if __name__ == '__main__':
    main()
